DROP TABLE IF EXISTS comments;
DROP TABLE IF EXISTS user_roles;
DROP TABLE IF EXISTS roles;
DROP TABLE IF EXISTS users;
DROP TABLE IF EXISTS routes;
DROP TABLE IF EXISTS airlines;
DROP TABLE IF EXISTS airports;
DROP TABLE IF EXISTS cities;
DROP TABLE IF EXISTS countries;

-- CREATING TABLES
CREATE TABLE users
(
    id         INT AUTO_INCREMENT PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name  VARCHAR(50) NOT NULL,
    username   VARCHAR(50) NOT NULL UNIQUE,
    password   VARCHAR(60) NOT NULL,
    PRIMARY KEY (id)
);

CREATE TABLE roles
(
    id   INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(10) NOT NULL,
    PRIMARY KEY (id)
);

CREATE TABLE user_roles
(
    user_id INT,
    role_id INT,
    PRIMARY KEY (user_id, role_id)
);

CREATE TABLE countries
(
    id   INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(50) NOT NULL UNIQUE,
    PRIMARY KEY (id)
);

CREATE TABLE cities
(
    id          INT AUTO_INCREMENT PRIMARY KEY,
    country_id  INT          NOT NULL,
    name        VARCHAR(50) NOT NULL,
    description VARCHAR(250) NOT NULL,
    PRIMARY KEY (id),
    UNIQUE (country_id, name),
    CONSTRAINT FK_cities_country_id FOREIGN KEY (country_id) REFERENCES countries (id)
);

CREATE TABLE airports
(
    id        INT AUTO_INCREMENT PRIMARY KEY,
    city_id   INT           NOT NULL,
    name      VARCHAR(100)  NOT NULL,
    iata      VARCHAR(3)  DEFAULT NULL,
    icao      VARCHAR(4)  DEFAULT NULL,
    latitude  DECIMAL(8, 6) NOT NULL,
    longitude DECIMAL(9, 6) NOT NULL,
    altitude  INT           NOT NULL,
    timezone  VARCHAR(5)    NOT NULL,
    dst       CHAR(1)       NOT NULL,
    olson     VARCHAR(50) DEFAULT NULL,
    type      VARCHAR(50) DEFAULT NULL,
    source    VARCHAR(50) DEFAULT NULL,
    PRIMARY KEY (id),
    CONSTRAINT FK_airports_city_id FOREIGN KEY (city_id) REFERENCES cities (id)
);

CREATE TABLE airlines
(
    id   INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    PRIMARY KEY (id),
    UNIQUE (name)
);

CREATE TABLE routes
(
    id                     INT AUTO_INCREMENT PRIMARY KEY,
    airline_id             INT          NOT NULL,
    source_airport_id      INT          NOT NULL,
    destination_airport_id INT          NOT NULL,
    codeshare              CHAR(1)      NOT NULL,
    stops                  INT          NOT NULL,
    equipment              VARCHAR(50) NOT NULL,
    price                  DECIMAL(6, 2),
    PRIMARY KEY (id),
    CONSTRAINT FK_routes_source_airport_id FOREIGN KEY (source_airport_id) REFERENCES airports (id),
    CONSTRAINT FK_routes_destination_airport_id FOREIGN KEY (destination_airport_id) REFERENCES airports (id),
    CONSTRAINT FK_routes_airline_id FOREIGN KEY (airline_id) REFERENCES airlines (id),
    UNIQUE (airline_id, source_airport_id, destination_airport_id)
);

CREATE TABLE comments
(
    id            INT AUTO_INCREMENT PRIMARY KEY,
    user_id       INT          NOT NULL,
    city_id       INT          NOT NULL,
    comment       VARCHAR(250) NOT NULL,
    date_added    DATETIME     NOT NULL,
    date_modified DATETIME DEFAULT NULL,
    PRIMARY KEY (id),
    CONSTRAINT FK_comments_user_id FOREIGN KEY (user_id) REFERENCES users (id),
    CONSTRAINT FK_comments_city_id FOREIGN KEY (city_id) REFERENCES cities (id)
);


